document.getElementById("menu-btn").addEventListener("click",function(){const e=document.getElementById("mobile-menu");e.classList.toggle("hidden")});function initializeSectionFadeIn(){const e=document.querySelectorAll("section[data-animate]");if(e.length===0)return;const t=new IntersectionObserver(e=>{e.forEach(e=>{e.isIntersecting&&(e.target.classList.add("fade-in-visible"),t.unobserve(e.target))})},{threshold:.1});e.forEach(e=>{t.observe(e)})}document.addEventListener("DOMContentLoaded",function(){initializeSectionFadeIn()})